import { useMemo, useState } from 'react';
// import { useNavigationStore } from '../../store/useNavigationStore';
// import * as THREE from 'three';
import { Text } from '@react-three/drei';

interface Hospital3DProps {
    onNodeClick?: (nodeId: string) => void;
    visibleFloor?: number;
}

export default function Hospital3D({ onNodeClick, visibleFloor = -1 }: Hospital3DProps) {
    // In a real app, we'd fetch the entire graph or a dedicated 3D model config
    // For now, let's mock some floors and rooms based on a grid to demonstrate multi-floor

    const floors = [1, 2, 3];
    const [hoveredNode, setHoveredNode] = useState<string | null>(null);

    // Mock rooms generation
    const rooms = useMemo(() => {
        const _rooms = [];
        for (const floor of floors) {
            // Create 4 rooms per floor for demo
            _rooms.push({ id: `room-${floor}-1`, x: -10, z: -10, floor, color: '#4ade80', label: `Room ${floor}01` });
            _rooms.push({ id: `room-${floor}-2`, x: 10, z: -10, floor, color: '#f87171', label: `Room ${floor}02` });
            _rooms.push({ id: `room-${floor}-3`, x: -10, z: 10, floor, color: '#60a5fa', label: `Room ${floor}03` });
            _rooms.push({ id: `room-${floor}-4`, x: 10, z: 10, floor, color: '#fbbf24', label: `Room ${floor}04` });

            // Corridors
            _rooms.push({ id: `corridor-${floor}`, x: 0, z: 0, floor, color: '#e5e7eb', type: 'corridor', w: 30, d: 5 });
            _rooms.push({ id: `corridor-v-${floor}`, x: 0, z: 0, floor, color: '#e5e7eb', type: 'corridor', w: 5, d: 30 });
        }
        return _rooms;
    }, []);

    return (
        <group>
            {rooms.map((room) => {
                const isHovered = hoveredNode === room.id;
                const isVisible = visibleFloor === -1 || visibleFloor === room.floor;
                const y = (room.floor - 1) * 15; // Vertical spacing between floors

                if (!isVisible) return null;

                return (
                    <group key={room.id} position={[room.x, y, room.z]}>
                        {/* Floor Base */}
                        <mesh
                            receiveShadow
                            position={[0, -0.1, 0]}
                            onClick={(e) => { e.stopPropagation(); onNodeClick?.(room.id); }}
                            onPointerOver={(e) => { e.stopPropagation(); setHoveredNode(room.id); }}
                            onPointerOut={() => setHoveredNode(null)}
                        >
                            <boxGeometry args={[room.w || 8, 0.2, room.d || 8]} />
                            <meshStandardMaterial
                                color={isHovered ? '#ffffff' : room.color}
                                transparent
                                opacity={0.9}
                            />
                        </mesh>

                        {/* Simulating Walls (Generic) */}
                        {room.type !== 'corridor' && (
                            <mesh position={[0, 2, 0]} castShadow>
                                <boxGeometry args={[8, 4, 8]} />
                                <meshStandardMaterial color={room.color} wireframe opacity={0.3} transparent />
                            </mesh>
                        )}

                        {/* Label */}
                        {room.label && (
                            <Text
                                position={[0, 5, 0]}
                                fontSize={1.5}
                                color="white"
                                anchorX="center"
                                anchorY="middle"
                                outlineWidth={0.1}
                                outlineColor="black"
                            >
                                {room.label}
                            </Text>
                        )}
                    </group>
                );
            })}
        </group>
    );
}

import { useEffect, useState } from 'react';
