export type NodeType = 'room' | 'corridor' | 'elevator' | 'stairs' | 'restroom' | 'exit' | 'emergency';

export interface Node {
    id: string;
    x: number;
    y: number;
    floorId: number;
    type: NodeType;
    label?: string;
    metadata?: Record<string, any>; // For additional searchable info
}

export interface Edge {
    id: string;
    from: string; // Node ID
    to: string; // Node ID
    weight: number; // Distance or cost
    floorId?: number; // Usually same as nodes, but for logic
    type?: 'walk' | 'elevator' | 'stairs';
}

export interface PathStep {
    nodeId: string;
    x: number;
    y: number;
    floorId: number;
    instruction?: string;
}

export interface NavigationPath {
    steps: PathStep[];
    totalDistance: number;
    estimatedTime: number; // in seconds
}

import { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { supabase } from '../lib/supabase';
import type { Doctor } from '../types';
import { useAuthStore } from '../store/useAuthStore';

export default function DoctorDetail() {
    const { id } = useParams<{ id: string }>();
    const navigate = useNavigate();
    const { user } = useAuthStore();

    const [doctor, setDoctor] = useState<Doctor | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    // Booking State
    const [selectedDate, setSelectedDate] = useState('');
    const [selectedTime, setSelectedTime] = useState('');
    const [bookingLoading, setBookingLoading] = useState(false);
    const [bookingSuccess, setBookingSuccess] = useState(false);

    useEffect(() => {
        async function fetchDoctor() {
            if (!id) return;
            try {
                setLoading(true);
                const { data, error } = await supabase
                    .from('doctors')
                    .select('*, hospitals(*)') // Join with hospitals
                    .eq('id', id)
                    .single();

                if (error) throw error;
                setDoctor(data);
            } catch (err: any) {
                console.error(err);
                setError(err.message || 'Failed to load doctor details');
            } finally {
                setLoading(false);
            }
        }
        fetchDoctor();
    }, [id]);

    const handleBooking = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!user) {
            navigate('/login');
            return;
        }
        if (!doctor || !selectedDate || !selectedTime) return;

        try {
            setBookingLoading(true);
            const { error } = await supabase
                .from('appointments')
                .insert([
                    {
                        doctor_id: doctor.id,
                        user_id: user.id,
                        appointment_date: selectedDate,
                        appointment_time: selectedTime,
                        status: 'pending'
                    }
                ]);

            if (error) throw error;

            setBookingSuccess(true);
            setTimeout(() => setBookingSuccess(false), 5000); // Reset success after 5s

            // Optional: reset form
            setSelectedDate('');
            setSelectedTime('');

        } catch (err: any) {
            alert('Booking failed: ' + err.message);
        } finally {
            setBookingLoading(false);
        }
    };


    if (loading) {
        return (
            <div className="flex items-center justify-center h-screen bg-gray-50">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
            </div>
        );
    }

    if (error || !doctor) {
        return (
            <div className="max-w-7xl mx-auto px-4 py-12 text-center">
                <h2 className="text-2xl font-bold text-red-600 mb-4">Error Loading Page</h2>
                <p className="text-gray-600 mb-6">{error || 'Doctor not found'}</p>
                <Link to="/" className="text-blue-600 hover:text-blue-800 underline">Back to Home</Link>
            </div>
        );
    }

    return (
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-12">

                {/* Profile Section */}
                <div className="md:col-span-2 space-y-8">
                    <div className="flex flex-col md:flex-row gap-6 items-start">
                        <div className="w-32 h-32 md:w-48 md:h-48 bg-gray-200 rounded-full overflow-hidden flex-shrink-0">
                            {doctor.image_url ? (
                                <img src={doctor.image_url} alt={doctor.name} className="w-full h-full object-cover" />
                            ) : (
                                <div className="w-full h-full flex items-center justify-center bg-blue-100 text-blue-500 text-4xl font-bold">
                                    {doctor.name.charAt(0)}
                                </div>
                            )}
                        </div>
                        <div>
                            <h1 className="text-3xl font-bold text-gray-900 mb-2">{doctor.name}</h1>
                            <p className="text-xl text-blue-600 font-medium mb-4">{doctor.specialization}</p>

                            <div className="flex flex-wrap gap-4 text-sm text-gray-600">
                                <span className="bg-gray-100 px-3 py-1 rounded-full">
                                    {doctor.experience_years} Years Experience
                                </span>
                                <span className="bg-green-100 text-green-700 px-3 py-1 rounded-full capitalize">
                                    {doctor.availability_status}
                                </span>
                            </div>

                            <p className="mt-6 text-gray-600 leading-relaxed">
                                Dr. {doctor.name} is a dedicated {doctor.specialization} with over {doctor.experience_years} years of experience.
                                Committed to providing patient-centered care and staying updated with the latest medical advancements.
                            </p>

                            {doctor.hospitals && (
                                <div className="mt-6 p-4 bg-gray-50 rounded-lg border border-gray-100">
                                    <p className="text-sm text-gray-500 mb-1">Affiliated Hospital</p>
                                    <Link to={`/hospital/${doctor.hospitals.id}`} className="font-semibold text-gray-900 hover:text-blue-600 transition-colors">
                                        ≡ƒÅÑ {doctor.hospitals.name}
                                    </Link>
                                    <p className="text-sm text-gray-500 mt-1">{doctor.hospitals.address}</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Booking Section */}
                <div className="space-y-6">
                    <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-100 sticky top-24">
                        <h3 className="text-xl font-bold text-gray-900 mb-6">Book Appointment</h3>

                        {bookingSuccess ? (
                            <div className="bg-green-50 border border-green-200 text-green-700 p-4 rounded-lg mb-6 text-center">
                                <p className="font-bold">Booking Confirmed!</p>
                                <p className="text-sm mt-1">Check "My Appointments" for details.</p>
                                <Link to="/my-appointments" className="block mt-3 text-sm underline hover:text-green-800">View Appointments</Link>
                            </div>
                        ) : (
                            <form onSubmit={handleBooking} className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">Date</label>
                                    <input
                                        type="date"
                                        required
                                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 outline-none transition-all"
                                        value={selectedDate}
                                        onChange={(e) => setSelectedDate(e.target.value)}
                                        min={new Date().toISOString().split('T')[0]}
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">Time</label>
                                    <input
                                        type="time"
                                        required
                                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 outline-none transition-all"
                                        value={selectedTime}
                                        onChange={(e) => setSelectedTime(e.target.value)}
                                    />
                                </div>

                                {user ? (
                                    <button
                                        type="submit"
                                        disabled={bookingLoading}
                                        className={`w-full py-3 rounded-lg font-bold text-white transition-all
                                            ${bookingLoading
                                                ? 'bg-blue-400 cursor-not-allowed'
                                                : 'bg-blue-600 hover:bg-blue-700 shadow-md hover:shadow-lg transform hover:-translate-y-0.5'
                                            }`}
                                    >
                                        {bookingLoading ? 'Booking...' : 'Confirm Appointment'}
                                    </button>
                                ) : (
                                    <Link
                                        to="/login"
                                        className="block w-full py-3 rounded-lg font-bold text-white bg-gray-800 hover:bg-black text-center transition-all shadow-md"
                                    >
                                        Login to Book
                                    </Link>
                                )}
                            </form>
                        )}

                        <p className="text-xs text-center text-gray-400 mt-4">
                            No payment required for booking. Pay at the clinic.
                        </p>
                    </div>
                </div>

            </div>
        </div>
    );
}

