export default function HospitalDetail() {
    const { id } = useParams<{ id: string }>();
    const [hospital, setHospital] = useState<Hospital | null>(null);
    const [departments, setDepartments] = useState<Department[]>([]);
    const [doctors, setDoctors] = useState<Doctor[]>([]);
    const [loading, setLoading] = useState(true);
    const [viewMode, setViewMode] = useState<'2d' | '3d'>('2d');
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        async function fetchHospitalData() {
            if (!id) return;

            try {
                setLoading(true);
                // 1. Fetch Hospital Details
                const { data: hospitalData, error: hospitalError } = await supabase
                    .from('hospitals')
                    .select('*')
                    .eq('id', id)
                    .single();

                if (hospitalError) throw hospitalError;
                setHospital(hospitalData);

                // 2. Fetch Departments
                const { data: deptData, error: deptError } = await supabase
                    .from('departments')
                    .select('*')
                    .eq('hospital_id', id);

                if (deptError) throw deptError;
                setDepartments(deptData || []);

                // 3. Fetch Doctors
                const { data: doctorData, error: docError } = await supabase
                    .from('doctors')
                    .select('*')
                    .eq('hospital_id', id);

                if (docError) throw docError;
                setDoctors(doctorData || []);

            } catch (err: any) {
                console.error(err);
                setError(err.message || 'Failed to display hospital details');
            } finally {
                setLoading(false);
            }
        }

        fetchHospitalData();
    }, [id]);

    if (loading) {
        return (
            <div className="flex items-center justify-center h-screen bg-gray-50">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
            </div>
        );
    }

    if (error || !hospital) {
        return (
            <div className="max-w-7xl mx-auto px-4 py-12 text-center">
                <h2 className="text-2xl font-bold text-red-600 mb-4">Error Loading Page</h2>
                <p className="text-gray-600 mb-6">{error || 'Hospital not found'}</p>
                <Link to="/hospitals" className="text-blue-600 hover:text-blue-800 underline">Back to Hospitals</Link>
            </div>
        );
    }

    return (
        <div className="space-y-12 pb-16">
            {/* Header */}
            <HospitalHeader hospital={hospital} />

            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 space-y-12">

                {/* Actions & Map Grid */}
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                    <div className="lg:col-span-2 space-y-8">
                        {/* About Section */}
                        <section className="bg-white p-6 rounded-xl shadow-sm border border-gray-100">
                            <h2 className="text-xl font-bold text-gray-900 mb-4">About</h2>
                            <p className="text-gray-600 leading-relaxed">
                                {hospital.name} is a leading healthcare facility committed to providing exceptional medical care.
                                Equipped with state-of-the-art technology and a team of experienced professionals, we ensure
                                the best outcomes for our patients.
                            </p>
                        </section>

                        {/* Departments Section */}
                        <section>
                            <h2 className="text-2xl font-bold text-gray-900 mb-6">Departments</h2>
                            <DepartmentList departments={departments} />
                        </section>

                        {/* Doctors Section */}
                        <section>
                            <div className="flex items-center justify-between mb-6">
                                <h2 className="text-2xl font-bold text-gray-900">Our Doctors</h2>
                                <Link to={`/doctors?hospital=${hospital.id}`} className="text-blue-600 hover:text-blue-800 text-sm font-medium">View All &rarr;</Link>
                            </div>
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                {doctors.slice(0, 4).map(doctor => (
                                    <DoctorCard key={doctor.id} doctor={doctor} />
                                ))}
                                {doctors.length === 0 && <p className="text-gray-500 italic">No doctors listed yet.</p>}
                            </div>
                        </section>
                    </div>

                    {/* Sidebar: Map & Contact */}
                    <div className="space-y-6">
                        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 sticky top-24">
                            <div className="flex justify-between items-center mb-4">
                                <h3 className="text-lg font-bold text-gray-900">Location</h3>
                                <div className="flex bg-gray-100 p-1 rounded-lg">
                                    <button
                                        onClick={() => setViewMode('2d')}
                                        className={`px-3 py-1 rounded-md text-xs font-medium transition-all ${viewMode === '2d' ? 'bg-white text-blue-600 shadow-sm' : 'text-gray-500 hover:text-gray-700'
                                            }`}
                                    >
                                        2D
                                    </button>
                                    <button
                                        onClick={() => setViewMode('3d')}
                                        className={`px-3 py-1 rounded-md text-xs font-medium transition-all ${viewMode === '3d' ? 'bg-white text-blue-600 shadow-sm' : 'text-gray-500 hover:text-gray-700'
                                            }`}
                                    >
                                        3D
                                    </button>
                                </div>
                            </div>
                            <div className="h-64 bg-gray-100 rounded-lg mb-4 relative overflow-hidden">
                                {viewMode === '2d' ? (
                                    <MapViewer label={hospital.name} />
                                ) : (
                                    <Suspense fallback={<div className="flex items-center justify-center h-full text-xs">Loading 3D...</div>}>
                                        <SceneContainer />
                                    </Suspense>
                                )}
                            </div>
                            <a
                                href={`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(hospital.name + ' ' + hospital.address)}`}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="block w-full text-center bg-blue-600 text-white py-3 rounded-lg font-medium hover:bg-blue-700 transition-colors"
                            >
                                Get Directions
                            </a>
                            <button
                                className="block w-full text-center mt-3 bg-white border border-gray-300 text-gray-700 py-3 rounded-lg font-medium hover:bg-gray-50 transition-colors"
                            >
                                Contact Hospital
                            </button>
                        </div>
                    </div>
                </div>

            </div>
        </div>
    );
}

import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '../lib/supabase';

export default function Login() {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        try {
            const { error } = await supabase.auth.signInWithPassword({
                email,
                password,
            });

            if (error) throw error;
            navigate('/'); // Redirect to home on success
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="flex min-h-[80vh] items-center justify-center bg-gray-50 px-4 py-12 sm:px-6 lg:px-8">
            <div className="w-full max-w-md space-y-8 bg-white p-8 rounded-xl shadow-lg border border-gray-100">
                <div className="text-center">
                    <h2 className="mt-6 text-3xl font-bold tracking-tight text-gray-900">Sign in to Medimap</h2>
                    <p className="mt-2 text-sm text-gray-600">
                        Or <Link to="/signup" className="font-medium text-blue-600 hover:text-blue-500">create a new account</Link>
                    </p>
                </div>

                {error && (
                    <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-md text-sm">
                        {error}
                    </div>
                )}

                <form className="mt-8 space-y-6" onSubmit={handleLogin}>
                    <div className="-space-y-px rounded-md shadow-sm">
                        <div>
                            <input
                                id="email-address"
                                name="email"
                                type="email"
                                autoComplete="email"
                                required
                                className="relative block w-full rounded-t-md border-0 py-2.5 px-3 text-gray-900 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:z-10 focus:ring-2 focus:ring-inset focus:ring-blue-600 sm:text-sm sm:leading-6"
                                placeholder="Email address"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </div>
                        <div>
                            <input
                                id="password"
                                name="password"
                                type="password"
                                autoComplete="current-password"
                                required
                                className="relative block w-full rounded-b-md border-0 py-2.5 px-3 text-gray-900 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:z-10 focus:ring-2 focus:ring-inset focus:ring-blue-600 sm:text-sm sm:leading-6"
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                    </div>

                    <div>
                        <button
                            type="submit"
                            disabled={loading}
                            className={`group relative flex w-full justify-center rounded-md bg-blue-600 px-3 py-2.5 text-sm font-semibold text-white hover:bg-blue-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-blue-600 transition-colors ${loading ? 'opacity-70 cursor-not-allowed' : ''}`}
                        >
                            {loading ? (
                                <span className="flex items-center gap-2">
                                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                                    Signing in...
                                </span>
                            ) : 'Sign in'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

