import { create } from 'zustand';
import type { NavigationPath } from '../navigation/types';
import { navigationEngine } from '../navigation/Pathfinder';

interface NavigationState {
    currentLocation: string | null; // Node ID
    destination: string | null; // Node ID
    path: NavigationPath | null;
    instructions: string[];
    isNavigating: boolean;

    // Actions
    setLocation: (nodeId: string) => void;
    setDestination: (nodeId: string) => void;
    calculatePath: () => void;
    startNavigation: () => void;
    stopNavigation: () => void;
    loadMapData: (nodes: any[], edges: any[]) => void;
    predictNextStep: (currentLocation: string, userRole: string) => Promise<{ nextNode: string, confidence: number, reasoning: string } | null>;
    fetchCrowdData: () => Promise<{ nodeId: string, density: number }[]>;
}

export const useNavigationStore = create<NavigationState>((set, get) => ({
    currentLocation: null,
    destination: null,
    path: null,
    instructions: [],
    isNavigating: false,

    setLocation: (nodeId) => set({ currentLocation: nodeId }),

    setDestination: (nodeId) => {
        set({ destination: nodeId });
        // Auto calculate on destination set if location is known
        if (get().currentLocation) {
            get().calculatePath();
        }
    },

    calculatePath: async () => {
        const { currentLocation, destination, fetchCrowdData } = get();
        if (!currentLocation || !destination) return;

        // Fetch real-time crowd data for optimization
        const crowdData = await fetchCrowdData();

        const path = navigationEngine.findPath(currentLocation, destination, crowdData);

        if (path) {
            const instructions = navigationEngine.getInstructions(path.steps);
            set({ path, instructions });
        } else {
            set({ path: null, instructions: ['Path not found or blocked by crowds'] });
        }
    },

    startNavigation: () => set({ isNavigating: true }),
    stopNavigation: () => set({ isNavigating: false, path: null, instructions: [] }),

    loadMapData: (nodes, edges) => {
        navigationEngine.loadGraph(nodes, edges);
    },

    // AI Actions
    predictNextStep: async (currentLocation: string, userRole: string) => {
        try {
            const response = await fetch('http://localhost:3001/api/predict-next', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ currentLocation, userRole })
            });
            const data = await response.json();
            return data;
        } catch (error) {
            console.error('AI Prediction Failed:', error);
            return null;
        }
    },

    fetchCrowdData: async () => {
        try {
            const response = await fetch('http://localhost:3001/api/route-weights');
            const data = await response.json();
            return data.crowdedNodes || [];
        } catch (error) {
            console.error('Failed to fetch crowd data', error);
            return [];
        }
    }
}));
